package com.company;

public class Problem115 {

    /*
    输入：S = "rabbbit", T = "rabbit"
    输出：3
    解释：*/

    public static void main(String[] args) {
        Problem115 p = new Problem115();
        long start = System.currentTimeMillis();
        System.out.println(p.numDistinct("",
                "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys"));
        System.out.println("cost: " + (System.currentTimeMillis() - start));
    }

    public int numDistinct(String s, String t) {
        if (s.length() < t.length()) {
            return 0;
        }

        int[][] dp = new int[s.length()+1][t.length()+1];
        for (int i = 0; i < dp.length; i++) {
            for (int j = 0; j < dp[0].length; j++) {
                dp[i][j] = -1;
            }
        }

        return numDistinct(s, t, 0, 0, dp);
    }


    /*
    输入：S = "rabbbit", T = "rabbit"
    输出：3
    解释：*/
    public int numDistinct(String s, String t, int index1, int index2, int[][] dp) {

        if (dp[index1][index2] != -1) {
            return dp[index1][index2];
        }

        if (t.length() == index2 && s.length() >= index1) {
            dp[index1][index2] = 1;
            return 1;
        }

        if (s.length() == index1 || (s.length() - index1) < (t.length() - index2)) {
            dp[index1][index2] = 0;
            return 0;
        }


        int sum = 0;
        if (s.getBytes()[index1] == t.getBytes()[index2]) {
            sum += numDistinct(s, t, index1 + 1, index2 + 1, dp);
        }

        sum += numDistinct(s, t, index1 + 1, index2, dp);
        dp[index1][index2] = sum;
        return sum;
    }
}
